from playwright.sync_api import Playwright, sync_playwright, expect

def run(playwright: Playwright) -> None:
    browser = playwright.chromium.launch(headless=False)
    context = browser.new_context()
    page = context.new_page()

    # Visit the homepage and log in
    page.goto("https://bookcart.azurewebsites.net/")
    page.get_by_role("button", name="Login").click()
    page.get_by_placeholder("Username").fill("siam495")
    page.get_by_placeholder("Password").fill("siamim7139@@@A")
    page.get_by_role("button", name="Login").nth(1).click()

    # Go to My Orders
    page.get_by_text("account_circlearrow_drop_down").click()
    page.get_by_role("menuitem", name="My Orders").click()

    # Search for a specific order
    page.get_by_placeholder("Search", exact=True).fill("258-853070")
    page.get_by_placeholder("Search", exact=True).press("Enter")

    context.close()
    browser.close()

with sync_playwright() as playwright:
    run(playwright)
